import React from 'react';
import PlanetTable from './components/BookTable';
import UserInput from './components/UserInput';
import MainHeader from './components/MainHeader';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      planetCollection: [],
    };
  }

  addNewPlanetToCollection = (newPlanet) => {
    const { planetCollection } = this.state;
    planetCollection.push(newPlanet);
    this.setState({ planetCollection });

    /*const { planetCollection } = this.state;
    const newPlanetsCollection = [ ...planetCollection ];
    newPlanetsCollection.push(newPlanet);
    this.setState({ planetCollection: newPlanet });*/
  };

  render(){
    return (
      <div className="App">
        <div className="container">
          <MainHeader />
          <div className="row">
            <UserInput onAddNewPlanet={this.addNewPlanetToCollection} />
            <PlanetsTable planetsCollection={this.state.planetsCollection} />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
